package com.capday1;

public class MyDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// int is a datatype and keyword //number is the name of variable
		int number = 10;
		System.out.println(number);

		// it is used to store fractional number //end with f and take 4 byte in
		// memory
		float f = 2.0f;
		System.out.println(f);

		// it is also used to store the reference value and takes 8 byte in
		// memeory
		double a = 2.00;
		System.out.println(a);

		// it is used to store char value and takes 1 byte memory
		char c = 'a';
		char ch = 97;
		System.out.println(c);
		System.out.println(ch);

		// string is class in java
		String s = "adv";
		System.out.println(s);

		// it is used to store boolean value true and false
		boolean b = false;
		System.out.println(b);

	}

}
